namespace Vy.Kundeservice.Models
{
    public class VyContext : DbContext
    {
        public VyContext()
        {
            Database.EnsureCreated();
        }

        public DbSet<FAQ> FAQ { get; set; }

        public DbSet<Hovedkategori> Hovedkategorier { get; set; }

        public DbSet<Underkategori> Underkategorier { get; set; }

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {

            // Opprett hovedkategorier
            var hovedKategori1 = new Hovedkategori() { Id = 1, Navn = "Billetter og setereservasjon" };
            var hovedKategori2 = new Hovedkategori() { Id = 2, Navn = "Endring, refusjon og kontrollgebyr" };
            var hovedKategori3 = new Hovedkategori() { Id = 3, Navn = "Bagasje og spesielle behov" };
            var hovedKategori4 = new Hovedkategori() { Id = 4, Navn = "Trafikk og ruter" };
            var hovedKategori5 = new Hovedkategori() { Id = 5, Navn = "Mat om bord" };
            var hovedKategori6 = new Hovedkategori() { Id = 6, Navn = "Apper, internett og strøm" };
            
            modelBuilder.Entity<Hovedkategori>().HasData(hovedKategori1, hovedKategori2, hovedKategori3, hovedKategori4, hovedKategori5, hovedKategori6);

            // Opprett underkategorier
            var underKategori1 = new Underkategori() { Id = 1, Navn = "Billetter og rabatter", HovedkategoriId = 1 };
            var underKategori2 = new Underkategori() { Id = 2, Navn = "Setereservasjon, Komfort og Sove", HovedkategoriId = 1 };
            var underKategori3 = new Underkategori() { Id = 3, Navn = "Samarbeid med lokale operatører", HovedkategoriId = 1 };

            var underKategori4 = new Underkategori() { Id = 4, Navn = "Endring, refusjon og kontrollgebyr", HovedkategoriId = 2 };

            var underKategori5 = new Underkategori() { Id = 5, Navn = "Bagasje og kjæledyr", HovedkategoriId = 3 };
            var underKategori6 = new Underkategori() { Id = 6, Navn = "Tilgjengelighet for funksjonshemmede", HovedkategoriId = 3 };
            var underKategori7 = new Underkategori() { Id = 7, Navn = "Reise med barn", HovedkategoriId = 3 };

            var underKategori8 = new Underkategori() { Id = 8, Navn = "Trafikk og ruter", HovedkategoriId = 4 };

            var underKategori9 = new Underkategori() { Id = 9, Navn = "Mat om bord", HovedkategoriId = 5 };

            var underKategori10 = new Underkategori() { Id = 10, Navn = "Vy-appen", HovedkategoriId = 6 };
            var underKategori11 = new Underkategori() { Id = 11, Navn = "Underholdningsappen", HovedkategoriId = 6 };
            var underKategori12 = new Underkategori() { Id = 12, Navn = "Internett og strøm", HovedkategoriId = 6 };

            modelBuilder.Entity<Underkategori>().HasData(underKategori1, underKategori2, underKategori3, underKategori4, underKategori5, underKategori6, underKategori7, underKategori8, underKategori9, underKategori10, underKategori11, underKategori12);

            // Opprett FAQs
            FAQ faq1 = new FAQ() { Id = 1, Spørsmål = "Hvordan kjøper jeg billett?", UnderkategoriId = 1, Rating = 0, Svar = "<li>Vy.no</li> <li>Appen</li> <li>Billettautomat</li> <li>Betjent stasjon</li> <li>Narvesen</li> <li>Om bord i toget</li> <li>Kundeservice</li> </ul> <h4>Vy.no</h4> <p>På vy.no får du kjøpt de fleste billetter ved å bruke reiseplanleggeren. Reiser du på Østlandet og i Hordaland, kan du på visse strekninger kjøpe 7 og 30 dagers elektronisk periodebillett på vy.no. Reisende i Rogaland kan kjøpe elektronisk periodebillett i Vy-appen eller periodebillett på reisekort hos Kolumbus. En periodebillett med setereservasjon kan kun kjøpes i Vy-appen.</p> <p>Betalingsmetoder:</p> <ul> <li>Kort (Visa, MasterCard, Diners)</li> <li>PayPal</li> <li>Vipps</li> <li>Faktura</li> <li>Gavekort</li> </ul> <h4>Appen</h4> <p>Har du iPhone eller Android-mobil, kan du kjøpe alle togbilletter i appen, inkludert periodebillett. Appen laster du ned i <a title=\"App Store\" href=\"https://itunes.apple.com/no/app/nsb/id439655098\" target=\"_blank\" rel=\"noopener\">App Store</a> eller <a title=\"Google Play\" href=\"https://play.google.com/store/apps/details?id=com.intele.nsbmob.app&amp;hl=en\" target=\"_blank\" rel=\"noopener\">Google Play</a>.</p> <p>Betalingsmetoder:</p> <ul> <li>Kort (Visa, MasterCard)</li> <li>Vipps</li> </ul> <h4>Billettautomat</h4> <p>På automaten kan du kjøpe de fleste togbilletter og hente ut forhåndsbestilte billetter.&nbsp;</p> <p>Betalingsmetoder:</p> <ul> <li>Kort</li> <li>Kontant (sedler kun hvis automat er innendørs)</li> </ul> <h4>Betjent stasjon</h4> <p>Er det et billettutsalg med rett til å selge togbilletter på din stasjon, kan du kjøpe eller hente ut togbilletter der.</p> <p>Betalingsmetoder:</p> <ul> <li>Kort</li> <li>Gavekort</li> <li>Faktura</li> </ul> <h4>Narvesen</h4> <p>Er det et billettutsalg eller en Narvesen-kiosk med rett til å selge togbilletter på din stasjon, kan du kjøpe eller hente ut togbilletter der.</p> <p>Du kan ikke kjøpe eller hente ut periodebilletter hos Narvesen.</p> <p>Betalingsmetoder:</p> <ul> <li>Kort</li> <li>Kontant</li> </ul> <h4>Om bord i toget</h4> <p>Reiser du fra en stasjon med billettautomat og kjøper billett på toget, koster det 40 kr ekstra. Ombordtillegg gjelder per reisende, med noen unntak som beskrevet under.</p> <p>Du må betale ombordtillegg selv om du ikke har fått kjøpt billett på grunn av fullt tog eller køer ved automat eller luke. Ombordtillegget gjelder for alle billettyper og kunder, med noen unntak. Det gjelder ikke for:</p> <ul> <li>Honnørreisende, blinde, svaksynte eller personer med en funksjonshemning som gjør at de ikke kan benytte automat</li> <li>Barn som reiser i følge med reisende som betaler honnør</li> <li>Reisende fra stasjoner uten billettautomat</li> <li>Hundebillett</li> <li>Sykkelbillett</li> <li>Produkttilleggene setereservasjon på korte regiontog, samt Komfort, Komfort Natt og Sove på lange regiontog</li> </ul> <p>Du kan ikke kjøpe periodebillett om bord.</p> <p>Betalingsmetoder:</p> <ul> <li>Kort</li> <li>Kontant (For reisende på toget mellom Halden og Gøteborg: Du kan ikke betale med svensk valuta om bord.)</li> </ul> <p>For reisende i Oslo og Akershus: Ruters tilleggsbillett kan ikke kjøpes om bord i tog. Tilleggsbilletten kjøper du i appen, eller på billettautomaten dersom du har reisekort.</p> <p>For reisende i Rogaland (Jærbanen): <a title=\"Les om kjøp av Kolumbus-billetter\" href=\"https://www.kolumbus.no/Billetter/slik-kjoper-du-billett/\" target=\"_blank\" rel=\"noopener\">Les om kjøp av Kolumbusbilletter</a>.</p> <h4>Kundeservice</h4> <p>Kundeservice kan hjelpe deg med kjøp av togbilletter til inn- og utland. <a title=\"Gå til kundeservice\" href=\"/kundeservice/hjelp-og-kontakt\" target=\"_top\">Se kontaktinformasjon og åpningstider for kundeservice</a>.</p> <p>Betalingsmetoder:</p> <ul> <li>Kort (Visa, MasterCard, Diners, Amex)</li> <li>Gavekort</li> <li>Betalingslenke sendt via e-post</li> <li>Faktura (for bedriftskunder)</li> </ul>" };
            FAQ faq2 = new FAQ() { Id = 2, Spørsmål = "Hvordan kjøper jeg periodebillett?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Det enkleste for deg er å kjøpe periodebilletten i appen. Vi tilbyr også periodebillett for Ruter, Kolumbus og Skyss. Enkelte varianter selger vi kun i appen.</p> <p>Du kan også kjøpe periodebillett på vy.no. <a title=\"Kjøp elektronisk periodebillett\" href=\"https://www.vy.no/togbilletter/kjop-periodebillett\" target=\"_top\">Kjøp periodebillett</a>.</p> <p>Du kan ikke kjøpe periodebillett om bord.</p> <p>Du kan kjøpe setereservasjon som en tilleggstjeneste til din periodebillett i appen.</p> <p>Periodebilletten er personlig, og kan ikke benyttes av andre.</p> <h4><strong>365-dagersbillett</strong><strong>&nbsp;–12 måneder for prisen av 10</strong></h4> <ul> <li>Gyldig i 365 dager fra den aktiveres</li> <li>Kjøpes per strekning på samme måte som andre periodebilletter</li> <li>Kan kjøpes med eller uten setereservasjon</li> <li>Kjøpes i appen eller hos kundeservice</li> </ul> <h4>Elektronisk periodebillett på Østlandet, Hordaland og Rogaland</h4> <p>Reiser du på Østlandet og i Hordaland, kan du på visse strekninger kjøpe 7 og 30 dagers elektronisk periodebillett på vy.no. Reisende i Rogaland kan kjøpe elektronisk periodebillett i Vy-appen eller periodebillett på reisekort hos Kolumbus. Ønsker du periodebillett med tilleggstjenesten setereservasjon må du kjøpe denne i Vy-appen.</p> <p>Du kan også fornye periodebilletten din via vår app.</p> <p><a title=\"Kjøp periodebillett\" href=\"https://www.vy.no/togbilletter/kjop-periodebillett\" target=\"_top\">Kjøp periodebillett</a>.</p> <h4>Periodebillett på papir</h4> <p>Første gang du skal kjøpe periodebillett på papir må du registrere deg slik at du får et kundenummer. Dette kan du gjøre på vy.no og i appen. Kundenummeret benyttes til å kjøpe periodebillett via automat eller på betjent stasjon.</p> <p>Trenger du hjelp til registrering kan du ta kontakt med kundeservice eller gå til billettsalget på en betjent stasjon.</p> <h4>Periodebillett for studenter og barn</h4> <p>Studenter under 30 år får cirka 40 prosent rabatt på 30 dagers periodebillett. Studentrabatten gjelder til og med den måneden du fyller 30 år. Legitimasjon og bekreftelse for betalt semesteravgift må vises. Studentrabatten gjelder ikke for 7-dagers periodebillett.</p> <p>Periodebillett med studentrabatt kan også kjøpes til barn fra 6 til og med 17 år. Ved billettkontroll må barnet fremvise gyldig periodebillett, samt personlig legitimasjon som også viser barnets alder. Som legitimasjon/aldersbevis gjelder bankkort (med bilde), pass, eller kopi av pass.</p> <h4>Ikke honnørrabatt på periodebillett</h4> <p>Vi gir ikke honnørrabatt på periodebillett, så du må i stedet kjøpe periodebillett for voksen.</p> <h4>Slik kjøper du en upersonlig periodebillett for å reise anonymt</h4> <p>Upersonlige periodebilletter kan bare kjøpes via upersonlige/anonyme reisekort. Upersonlig/anonymt reisekort kan skaffes på betjent stasjon eller via kundeservice. Kjøp av upersonlig periodebillett til reisekortet gjør du via vy.no eller i billettautomat.</p> <p>Upersonlig periodebillett kan benyttes av andre, men man må kunne legitimere sin rett til eventuell rabatt på periodebilletten, for eksempel studentrabatt. Upersonlig periodebillett erstattes ikke ved tap av reisekort, og gir ikke rett til refusjon ved sykdom.</p>" };
            FAQ faq3 = new FAQ() { Id = 3, Spørsmål = "Hvordan henter jeg billetter kjøpt på vy.no?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Det enkleste er å hente ut billetten via appen. Den kan også lastes ned som PDF eller hentes ut om bord, på billettautomat, på betjent stasjon og hos enkelte Narvesen-kiosker.</p> <h4>App for iPhone og Android</h4> <p>Har du iPhone eller Android kan du hente ut billetter i appen vår. (Appen laster du ned i <a title=\"App Store\" href=\"https://itunes.apple.com/no/app/nsb/id439655098\" target=\"_blank\" rel=\"noopener\">App Store</a> eller <a title=\"Google Play\" href=\"https://play.google.com/store/apps/details?id=com.intele.nsbmob.app\" target=\"_blank\" rel=\"noopener\">Google Play</a>). Billettene vil kun ligge på enheten du lastet ned billettene på, så denne må medbringes på toget.&nbsp;</p> <p>Når du har kjøpt en billett på vy.no, vil du få en kvittering med hentekode. I appen kan du gå på «Vis billett» og «Last ned». Her skriver du inn hentekoden for å laste ned billetten. Dette kan være praktisk for deg som kjøper billett for andre, som for eksempel barn som ikke har eget kredittkort.</p> <p>Det er ditt ansvar at du har en gyldig billett tilgjengelig før du går om bord på toget. Forutsetningen for å kunne reise med toget er at du kan vise gyldig billett ved kontroll. Har du kjøpt billett via mobilen, må denne også være tilgjengelig ved billettkontroll. Sørg derfor for å ha nok batteri på mobilen for hele reisen.</p> <h4>Skriv ut billetten hjemme eller ha den digitalt</h4> <p>Med PDF-billett kan du laste ned billetten din selv og ta den med på toget. Du kan skrive den ut på papir eller ha den med på mobil, nettbrett eller PC. PDF-billett tilbys for reiser som inkluderer minst én setereservasjon, samt for grenseoverskridende reiser mellom Oslo og Gøteborg. Du må ha et program for å laste ned PDF-filer, som for eksempel Adobe Reader.</p> <p><a title=\"Last ned gratis Adobe Reader\" href=\"https://get.adobe.com/no/reader/\" target=\"_blank\" rel=\"noopener\">Last ned gratis Adobe Reader</a>.</p> <h4>Billettautomat</h4> <p>Skal du hente ut billetter på våre billettautomater taster du inn hentekode. Hentekoden står på kvitteringse-posten din og finnes i kjøpshistorikken på vy.no.</p> <h4>Betjent stasjon, Narvesen eller om bord i toget</h4> <p>Er det et billettutsalg eller en Narvesen på din stasjon kan du hente ut billettene dine der. Du må bruke referansenummer ved uthenting på Narvesen. Du kan ikke hente ut periodebillett på Narvesen.</p> <p>Ved uthenting av billett om bord, oppgi referansenummeret og navnet til hovedreisende når konduktøren kommer til deg.</p> <p>Hentekode og referansenummer vil stå på kvitteringse-posten din og finnes i kjøpshistorikken på vy.no.</p>" };
            FAQ faq4 = new FAQ() { Id = 4, Spørsmål = "Hvem kan få rabatt?", UnderkategoriId = 1, Rating = 0, Svar = "<p><a title=\"Les om rabatt på togbilletter\" href=\"/alt-om-reisen/tog/billetter/rabatter\" target=\"_top\">Les om rabatt på togbilletter</a></p>" };
            FAQ faq5 = new FAQ() { Id = 5, Spørsmål = "Det står feil navn på billetten. Hva gjør jeg?", UnderkategoriId = 1, Rating = 0, Svar = "<p><a title=\"Se spørsmålet «Hvordan endrer jeg navn på billetten?»\" href=\"/kundeservice/sporsmal-og-svar/endring-refusjon-og-kontrollgebyr?item=1850\" target=\"_top\">Se spørsmålet «Hvordan endrer jeg navn på billetten?»</a></p>" };
            FAQ faq6 = new FAQ() { Id = 6, Spørsmål = "Hvordan finner jeg Miniprisbilletter og hva er betingelsene?", UnderkategoriId = 1, Rating = 0, Svar = "<h4>Betingelser</h4> <p>Billetten må kjøpes senest én dag før reisedagen, og kan ikke avbestilles, endres eller refunderes. Miniprisbilletter kan ikke kombineres med andre rabatter som student eller honnør. Ved kjøp av Minipris via kundeservice eller betjent stasjon er det en tilleggskostnad på 50 kr per billett per person.</p> <p><a title=\"Les mer om Minipris\" href=\"/alt-om-reisen/tog/billetter/minipris\" target=\"_top\">Les mer om Minipris</a>.</p>" };
            FAQ faq7 = new FAQ() { Id = 7, Spørsmål = "Hva er reisekort?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Med reisekort reiser du med elektronisk billett hos Vy og Ruter, som samarbeider om kortsystemet elektronisk reisekort. Reisekortet gjelder i Oslo og Akershus.</p> <p>Du kan kjøpe reisekort fra kundeservice eller på betjent stasjon.</p> <p><a title=\"Les om personvern og reisekort\" href=\"/vilkar-og-personvern/reisekort-og-personvern\" target=\"_top\">Les om personvern og reisekort</a>.</p>" };
            FAQ faq8 = new FAQ() { Id = 8, Spørsmål = "Hvordan kjøper jeg gavekort, og hvordan betaler jeg med det?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Du kan ikke lenger kjøpe gavekort, men kunder kan få gavekort av oss i ulike sammenhenger.</p> <p>Verdikoden på gavekortet kan brukes til kjøp av enkeltbilletter eller periodebilletter til Vys tog via vy.no, kundeservice og betjente stasjoner.</p> <p>Billetter kan betales helt eller delvis med gavekort, og gavekortet kan benyttes i flere omganger.</p> <p>Gavekort kan ikke brukes til å kjøpe utenlandsbilletter på vy.no, men det kan brukes som betalingsmiddel for utenlandsbilletter og kundekort på betjente stasjoner og kundeservice.</p>" };
            FAQ faq9 = new FAQ() { Id = 9, Spørsmål = "Hvordan kjøper jeg Interrailbillett?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Du kan kjøpe Interrailbillett via kundeservice, betjente stasjoner eller på <a title=\"europarundt.no\" href=\"https://europarundt.no/\" target=\"_blank\" rel=\"noopener\">europarundt.no</a>. <a title=\"Les mer om Interrail\" href=\"/alt-om-reisen/tog/billetter/interrail\" target=\"_top\">Les mer om Interrail</a>.</p>" };
            FAQ faq10 = new FAQ() { Id = 10, Spørsmål = "Jeg vil reise rundt i Norge i ferien. Finnes det en billett for dette?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Vi tilbyr ikke en fleksibel billett som kan brukes på flere avganger rundt omkring i Norge. Det er heller ikke mulig å reise med Interrailbillett i eget land. For å reise rundt i Norge med toget, må du dermed bestille enkeltbilletter for de avgangene du ønsker.</p> <p>Hvis du er interessert i en norgesferie med transport, hotell og aktiviteter inkludert, så kan du se om noen av turene til vår samarbeidspartner, Fjord Tours, passer deg.</p> <p><a title=\"Les mer om norgesferie med Fjord Tours\" href=\"/alt-om-reisen/opplevelsesreiser/norgesferie\">Les mer om norgesferie med Fjord Tours</a>.</p> <p><a title=\"Les mer om turen Norge i et nøtteskall®\" href=\"/alt-om-reisen/opplevelsesreiser/norge-i-et-notteskall\" target=\"_top\">Les mer om turen Norge i et nøtteskall®</a>.</p>" };
            FAQ faq11 = new FAQ() { Id = 11, Spørsmål = "Hvor lenge før avreise kan jeg kjøpe togbillett?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Billetter legges ut for salg 90 dager før avgang. Det kan være kortere salgsperiode ved planlagte arbeider på strekningen eller i forbindelse med ruteendringer i juni og desember.</p>" };
            FAQ faq12 = new FAQ() { Id = 12, Spørsmål = "Jeg vil betale for togbilletten med faktura – hvordan gjør jeg det?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Privatpersoner kan velge faktura ved betaling på vy.no. Minstebeløpet er 500 kr og du må betale fakturagebyr på 40 kr.</p> <p>Fakturabetaling via kundeservice og betjent stasjon er forbeholdt firmaer, reisebyråer, skoler, institusjoner, foreninger, lag og grupper, samt privatpersoner som kjøper årskort. </p>" };
            FAQ faq13 = new FAQ() { Id = 13, Spørsmål = "Hva skjer hvis jeg ikke rekker toget?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Enkeltbilletter er gyldig til angitt avgang, det vil si at du ikke kan benytte billetten på andre avganger. Du må dermed kjøpe en ny billett hvis du vil reise med neste avgang.</p> <p>Dersom du bruker Vy-appen til å kjøpe Ruter-billett, er det Ruters bestemmelser for gyldighetstid som gjelder.&nbsp;</p>" };
            FAQ faq14 = new FAQ() { Id = 14, Spørsmål = "Det står at det er utsolgt. Kan jeg bli med likevel?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Det er ikke mulig å kjøpe billetter til utsolgte tog. Du kan møte opp og ta sjansen på at det er kapasitet. Konduktøren gjør da en vurdering rett før avgang. Dersom det ikke er kapasitet om bord vil du ikke kunne bli med toget.</p>" };
            FAQ faq15 = new FAQ() { Id = 15, Spørsmål = "Jeg får feilmelding når jeg betaler på vy.no – hva gjør jeg?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Det kan være mange årsaker til dette, som for eksempel:</p> <ul> <li>Det er noe med nettleseren din som gjør at ikke kjøpsløsningen fungerer optimalt. Vi anbefaler at du prøver igjen med Chrome.</li> <li>Hvis profilen din er registrert med et telefonnummer som inneholder spesialtegnet +, så kan dette føre til feil ved betaling.</li> <li>Det kan være noe feil med det lagrede betalingskortet ditt. Prøv å slette dette og lagre det på nytt.&nbsp;</li> <li>Hvis du har prøvd å kjøpe billett til en utsolgt avgang vil betalingen feile, så forsøk med en annen avgang.</li> </ul> <p>Hvis du fremdeles trenger hjelp, kontakt kundeservice.</p>" };
            FAQ faq16 = new FAQ() { Id = 16, Spørsmål = "Jeg har kjøpt billett på vy.no. Hvorfor har jeg ikke fått kvittering på e-post?", UnderkategoriId = 1, Rating = 0, Svar = "<p>Det kan være forskjellige grunner til dette:</p> <ul> <li>Du var ikke innlogget ved kjøp og oppga feil e-postadresse. Ta i så fall kontakt med kundeservice for å få tilsendt kvittering.</li> <li>Hvis du var innlogget, sjekk e-postadressen tilknyttet din Vy-profil – kanskje det var en annen adresse enn den du pleier å bruke?</li> <li>Det kan hende e-posten har blitt sortert som søppelpost, så sjekk dette. Dette er spesielt vanlig for kunder som benytter e-postadresse tilknyttet bedriften sin.</li> </ul> <p>Det har i korte perioder vært feil i baksystemene våre, som fører til at e-poster ikke blir sendt. Da blir e-postene sendt ut umiddelbart etter feilen er fikset.</p> <p>Trenger du fremdeles hjelp? Ta kontakt med kundeservice.</p>" };
            FAQ faq17 = new FAQ() { Id = 17, Spørsmål = "Hvordan får jeg kjøpt eller fornyet kundekort?", UnderkategoriId = 1, Rating = 0, Svar = "<p><a title=\"Les om kundekort\" href=\"/alt-om-reisen/tog/billetter/rabatter?item=783\" target=\"_top\">Les om kundekort</a></p>" };

            FAQ faq18 = new FAQ() { Id = 18, Spørsmål = "Kan jeg reservere sete om bord?", UnderkategoriId = 2, Rating = 0, Svar = "<p>På lange regiontog, Raumabanen og Rørosbanen er setereservasjon inkludert i prisen. Når du bestiller billetten på nett eller i appen er det et steg der du velger hvor i toget du ønsker å sitte.</p> <p>I slike tog kan du reservere en ledig plass ved siden av deg mot prisen av en ordinær voksenbillett eller Miniprisbillett.</p> <p>På korte regiontog på Østlandet kan du kjøpe setereservasjon som et tillegg til din enkeltbillett eller til din periodebillett. Med setereservasjon kan du ved hjelp av et setekart reservere et valgfritt sete i en egen setereservasjonsvogn – til en rimelig pris. Dette kan du gjøre på avganger flere uker frem i tid. Seter som ikke er reservert kan brukes av andre reisende. Slik får vi benyttet hele toget.&nbsp;</p> <p><a title=\"Les mer om setereservasjon på korte regiontog på Østlandet\" href=\"/alt-om-reisen/tog/billetter/setereservasjon-pa-korte-regiontog\" target=\"_top\">Les mer om setereservasjon på korte regiontog på Østlandet</a>.</p> <p>&nbsp;</p>" };
            FAQ faq19 = new FAQ() { Id = 19, Spørsmål = "Hvordan får jeg plass i Komfort?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Komfort selges som et tillegg til billetter på lange regiontog og koster 100 kr per person uansett hvor langt du reiser. Du kan velge Komfort når du kjøper billetten på vy.no eller i appen. Du kan også kjøpe Komfort om bord av konduktøren, hvis det er ledig plass.</p> <h4>Komfort er tilgjengelig på regiontog på strekningene:</h4> <ul> <li>Oslo–Trondheim</li> <li>Oslo–Kristiansand–Stavanger</li> <li>Oslo–Bergen</li> <li>Trondheim–Bodø</li> </ul> <h4>Komfortavdelingen merkes med setenummer</h4> <p>Setenummereringen av Komfortavdelingen gjør det lettere for deg som kunde å finne plassen din.&nbsp;</p>" };
            FAQ faq20 = new FAQ() { Id = 20, Spørsmål = "Hvordan får vi plass i Familie?", UnderkategoriId = 2, Rating = 0, Svar = "<p><a title=\"Se temaet «Reise med barn»\" href=\"/kundeservice/sporsmal-og-svar/bagasje-og-spesielle-behov\" target=\"_top\">Se temaet «Reise med barn»</a>.</p>" };
            FAQ faq21 = new FAQ() { Id = 21, Spørsmål = "Hvordan får jeg plass i Stille?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Du kan ikke reservere plass i Stille, men du kan sette deg der hvis det er ledig plass.</p> <p>I denne vognen gjelder følgende regler:</p> <ul> <li>Ikke snakk i telefonen</li> <li>Sett deg i en annen vogn hvis du ønsker å prate</li> <li>Ikke spill musikk så høyt at du plager andre</li> <li>Ikke bruk PC med tastatur</li> <li>Bruk gjerne annet datautstyr med touch</li> </ul> <p>Stille finnes på strekningene:</p> <ul> <li>Oslo–Halden–Göteborg</li> <li>Skien–Oslo–Lillehammer</li> <li>Steinkjer–Støren</li> </ul>" };
            FAQ faq22 = new FAQ() { Id = 22, Spørsmål = "Hvordan får jeg plass i Sove?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Vi tilbyr sovevogn på fire strekninger: Oslo–Trondheim, Oslo–Kristiansand–Stavanger, Oslo–Bergen og Trondheim–Bodø.</p> <p>Du kjøper plass i sovekupé som et tillegg til vanlige billetter og det koster 950 kr per kupé. Når du kjøper billett på vy.no og i appen kan du velge mellom sovekupé eller ordinær plass.</p>" };
            FAQ faq23 = new FAQ() { Id = 23, Spørsmål = "Hvordan er sovekupeene og hvor mange får plass i hver kupé?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Sovevognene har komfortable sovekupeer med to senger (75 cm brede) med gode madrasser, dyner og puter. Det er tillatt med inntil to voksne og to barn per kupé. Vi anbefaler egen seng til barn over fire år. Barn under 6 år reiser gratis i sovekupéen.</p> <p>Strømuttak, håndklær, såpe og drikkevann finnes i kupeen. Det er ikke tillatt med dyr dersom du reiser i sovekupé. Du kan ta med sammenslått barnevogn som bagasje.</p>" };
            FAQ faq24 = new FAQ() { Id = 24, Spørsmål = "Får jeg frokost når jeg har plass i Sove?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Reiser du med Sove får du tilbud om rabattert pris på frokost på våre samarbeidshotell nær togstasjonene. Noen av hotellene har også dusjmuligheter.</p> <p>Det er ingen forhåndsbestilling, og du betaler direkte til hotellet. Vis frem billetten din fra Sove og nyt en god start på dagen. Samarbeidshoteller:</p> <ul> <li>Oslo: Thon Hotel Opera</li> <li>Bergen: Hotel Grand Terminus</li> <li>Stavanger: Clarion Hotel Stavanger</li> <li>Trondheim: Best Western Chesterfield Hotel</li> </ul> <p>Mer informasjon om ordningen med prisoversikter og veibeskrivelser til hotellene finner du i sovekupéen.</p>" };
            FAQ faq25 = new FAQ() { Id = 25, Spørsmål = "Hvordan sjekker jeg inn i sovekupeen og hva er inn- og utsjekkingstidene?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Du sjekker inn i kafévognen før du går til sovekupeen.</p> <h4>Innsjekkingstider:</h4> <p>Oslo–Trondheim kl. 22:15<br>Trondheim–Oslo kl. 22:10</p> <p>Oslo–Bergen kl. 22:35<br>Bergen–Oslo kl. 22:15</p> <p>Trondheim–Bodø kl. 23:05<br>Bodø–Trondheim kl. 20:50</p> <p>Oslo–Stavanger kl. 22:05<br>Stavanger–Oslo kl. 22:00</p> <p>Vi kan videreselge kupeen på Sove dersom du ikke reiser fra stasjonen det er kjøpt billett fra og ikke har sagt fra til konduktøren innen en halv time etter avgang.</p> <h4>Kupeen kan disponeres frem til:</h4> <p>Oslo–Trondheim kl. 07:00<br>Trondheim–Oslo kl. 07:15</p> <p>Oslo–Bergen kl. 07:30<br>Bergen–Oslo kl. 07:00</p> <p>Trondheim–Bodø kl. 09:15<br>Bodø–Trondheim kl. 07:50</p> <p>Oslo–Stavanger kl. 07:30<br>Stavanger–Oslo kl. 07:45</p> <p>Vekking skjer over høyttaler 15 minutter før du går av toget på endestasjonen. Skal du av tidligere, må du ta ansvar for vekkingen selv.</p>" };
            FAQ faq26 = new FAQ() { Id = 26, Spørsmål = "Hvordan får jeg plass i Komfort Natt?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Vi tilbyr Komfort Natt kun på Sørlandsbanens (Oslo–Kristiansand–Stavanger–Oslo) nattog alle netter, bortsett fra natt til søndag. Det koster 230 kr i tillegg til vanlig billettpris.</p>" };
            FAQ faq27 = new FAQ() { Id = 27, Spørsmål = "Kan jeg sitte i vanlig sete på nattoget?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Ja, og med sitteplass på nattoget får du en pakke med pute, pledd, øyemaske og ørepropper inkludert i prisen. Pakken henter du i kafévognen. Ta gjerne med deg pakken hjem, hvis ikke doneres pleddene til veldedig formål. De fleste stolene på nattoget kan justeres bakover.</p>" };
            FAQ faq28 = new FAQ() { Id = 28, Spørsmål = "Har dere dyrefri sone for meg som er allergisk?", UnderkategoriId = 2, Rating = 0, Svar = "<p>Togene våre har en dyrefri sone der det ikke er tillatt å ha med seg dyr.</p> <p>På tog med setereservasjon kan du reservere plass i dyrefri sone, mens på tog uten plassreservering finner du vogn merket med dyrefri-symbolet.</p>" };

            FAQ faq29 = new FAQ() { Id = 29, Spørsmål = "Hvordan er samarbeidet mellom Vy og Ruter?", UnderkategoriId = 3, Rating = 0, Svar = "<h4>Tilleggsbillett</h4> <p>Har du en gyldig billett som gjelder i én eller flere soner i Oslo og Akershus, kan du forlenge reisen med en Ruter tilleggsbillett. Tilleggsbilletten må kjøpes rett før en reise, og er ikke tilgjengelig for salg om bord i togene. Tilleggsbilletten kjøper du i Vy-appen hvis du har en gyldig billett, eller på billettautomaten dersom du har reisekort. Tilleggsbilletten er kun gyldig for den reisen den er kjøpt til, og kan ikke benyttes ved senere reiser.</p> <p><a title=\"sonekart\" href=\"https://ruter.no/kjop-billett/soner/\" target=\"_blank\" rel=\"noopener\">Se sonekart for Oslo og Akershus (Ruter)</a></p> <p>Ruter og Vy har ulike takstsystemer. Billettene må derfor kjøpes som to separate billetter.</p> <p>Vy selger egne periodebilletter med overgang i Ruter sone 1. For å benytte denne må overgangsstasjonen være den togstasjonen i Oslo hvor du faktisk foretar overgang mellom Vys tog og Ruters transportmidler.</p> <h4>Kombinasjon av Ruter- og Vy-billetter</h4> <ul> <li>Ruter enkeltbillett kan kombineres med Vy periodebillett, men ikke med Vy enkeltbillett</li> <li>Ruters tilleggsbillett kan ikke kombineres med Vy periodebillett</li> <li>Ruter periodebillett (7 dager, 30 dager og 365 dager) kan kombineres med Vy enkeltbillett uten setereservasjon eller Vy periodebillett uten setereservasjon</li> <li>Grunnskolebillett og skolebillett kan kombineres med Vy enkeltbillett uten setereservasjon</li> </ul> <p>Det er siste/første Vy-stasjon i den sonen Ruterbilletten gjelder som skal benyttes som fra/til-stasjon ved kombinasjon med Vy billett. Dersom du har Ruter periodebillett sone 1, må følgende stasjoner benyttes ved kombinasjon med Vy-billetter: Stabekk, Movatn, Lørenskog og Rosenholm. For en reise på Østfoldbanen sørover til Moss blir for eksempel Rosenholm den stasjonen du skal kjøpe enkeltbillett fra.</p> <p>Når du kombinerer periodebilletter fra Vy og Ruter behøver ikke&nbsp;toget stoppe på stasjonen som brukes som «skjøtestasjon», samtidig kan ikke stasjonen ligge på en annen linje enn den toget kjører på. Her benyttes linjekartet som blant annet henger i togene som fasit.&nbsp;Eksempel: Periodebillett Oslo S–Sonsveien kan skjøtes med periodebillett Sonsveien–Moss, selv på regiontog som ikke har stopp på Sonsveien.</p> <p>Ruterbilletter for sone 4S (Moss/Kambo) kan du ikke bruke på toget, kun på bussen.</p> <h4>Familierabatt</h4> <p>For reiser som starter og slutter i Oslo og Akershus gjelder Ruter sin familierabatt på lørdager, søndager og offentlige fridager. Reisende over 18 år med gyldig billett for voksen, honnør eller student kan ha med seg inntil fire barn gratis. Barn under seks år telles ikke med i dette antallet da de alltid reiser gratis.</p>" };
            FAQ faq30 = new FAQ() { Id = 30, Spørsmål = "Hvordan er samarbeidet mellom Vy og Kolumbus?", UnderkategoriId = 3, Rating = 0, Svar = "<p>Vy og Kolumbus har billettsamarbeid på lokaltog.</p> <p>Følgende Kolumbus-billetter er også gyldige på Vy lokaltog på Jærbanen for de soner du har kjøpt billett for:</p> <ul> <li>Enkeltbilletter</li> <li>24-timersbillett</li> <li>7-dagers periodebillett</li> <li>30-dagers periodebillett</li> <li>30-dagers kombibillett (kun Voksen, gjelder sone Nord-Jæren Stavanger–Gandal og utvalgte båtruter)</li> <li>Hjemjobbhjem</li> <li>Ungdomsbillett</li> <li>Skolekort</li> <li>365-dagersbillett</li> </ul> <p><a title=\"sonekart\" href=\"https://www.kolumbus.no/Billetter/soner-og-sonekart/\" target=\"_blank\" rel=\"noopener\">Kolumbus sonekart </a></p> <p>Alle periodebilletter fra Kolumbus er også gyldige på regiontogene i Rogaland. Kolumbus enkeltbillett er ikke gyldig på regiontogene, her gjelder Vys egne billetter.</p> <p>Du kan ikke kombinere Kolumbus- og Vy-billetter. Kolumbus-billetter kan ikke brukes som delbetaling på Vy regiontog.</p> <p>Ungdomsbilletten kan brukes av ungdom fra og med 15 år til og med 22 år.&nbsp;Den er kun gyldig på lokaltogene mellom Stavanger og Egersund (Jærbanen). Unntaket er de som skal reise fra/til Moi. Ungdomsbilletten kan også benyttes mellom Moi og Sira, dersom du kan vise skolekort fra videregående skole i Flekkefjord. Den er ikke gyldig mellom Moi og Sira i helger og skoleferier. Ungdomsbilletten&nbsp;kan ikke brukes som delbetaling. Dersom du skal reise til stasjoner sør for Sira må du kjøpe en egen Vy-billett for hele reisen fra start til slutt. <a title=\"Les mer om ungdomsbillett på kolumbus.no\" href=\"https://www.kolumbus.no/Billetter/-priser-og-produkter/ungdomsbillett/\" target=\"_blank\" rel=\"noopener\">Les mer om ungdomsbillett på kolumbus.no</a>.&nbsp;</p> <p>Det er Kolumbus aldersgrenser og rabatter for studenter som gjelder på lokaltogene på Jærbanen. Dette kan du lese mer om her: Kolumbus. På Vys regiontog gjelder Vys aldersgrenser og rabatter for student enkeltbillett i Rogaland. Kolumbus periodebillett student kan benyttes på regiontogene i Rogaland.</p> <p>Kolumbus familietilbud for to voksne og tre barn etter kl. 17 og i helgene gjelder på lokaltog. Kolumbus priser for sykkel og hund gjelder på lokaltogene på Jærbanen.</p> <h4>Kjøp billetten i Vy-appen</h4> <p>Du kan kjøpe de fleste Kolumbus-billettene i Vy-appen. I tillegg kan du kjøpe enkeltbillett på automater på stasjonen og fra konduktøren. Betjent salg i Stavanger selger alle Kolumbus-billetter. Kolumbus selger billetter på egen app, til reisekort og på papir. HjemJobbHjem selges kun hos Kolumbus. Vy godtar disse billettene om bord i togene.</p> <p>Alle Kolumbus-billetter unntatt HjemJobbHjem og skolekort, er upersonlige og kan deles med andre. Den som låner billetten, må tilfredsstille kravene til billetten han/hun låner. Det eneste som kreves er at du kan dokumentere at du har krav på den rabatten som periodebilletten er kjøpt med.</p> <p>Kolumbus sine billettbestemmelser gjelder ved kjøp av Kolumbus-billetter. Det er Vys transportvilkår som gjelder for togreisen.</p>" };
            FAQ faq31 = new FAQ() { Id = 31, Spørsmål = "Hvordan er samarbeidet mellom Vy og Skyss?", UnderkategoriId = 3, Rating = 0, Svar = "<h4>Skyss 30-dagers ungdomsbillett</h4> <p>Skyss 30-dagers ungdomsbillett solgt via Skysskort og Skyss app er gyldig på alle Vy tog i Hordaland. Kunder med billett på Skysskort må vise kvittering for gyldig billett.</p> <h4>Skyss 30-dagers periodebillett</h4> <ul> <li>Reiser med tog mellom Bergen–Arna–Trengereid inklusiv reiser med buss innenfor sone A: 30-dagers periodebillett for voksen, student, barn og honnør solgt via Vy-appen eller Skyss app er gyldig på Vys rute 43 (Bergen–Arna) og rute 45 (Bergen–Voss–Myrdal). 30 dagers periodebillett Sone A på Skysskort er ikke gyldig på Vy sine tog.</li> <li>Reiser med tog mellom Bergen–Arna–Trengereid inklusiv reiser med buss innenfor sone A og F: 30-dagers periodebillett for voksen, student, barn og honnør solgt via Skyss app er gyldig på Vy rute 43 (Bergen–Arna) og rute 45 (Bergen–Voss–Myrdal). 30 dagers periodebillett Sone A og F på Skysskort er ikke gyldig på Vy sine tog.</li> <li>Reiser fra stasjonene Bergen, Arna eller Trengereid og til stasjonene øst for Trengereid (Vaksdal–Voss): 30-dagers periodebillett buss/tog for voksen, barn og student solgt på Skysskort og Vy-appen er gyldig på rute 43 (Bergen–Arna) og rute 45 (Bergen–Voss–Myrdal). Kunder med billett på Skysskort må vise kvittering for gyldig billett.</li> </ul> <h4>Følgende Skyss-billetter gjelder ikke på tog i Hordaland:</h4> <ul> <li>Skyss enkeltbilletter</li> <li>Skyss 7-dagers periodebillett</li> <li>Skyss 24-timersbillett</li> </ul> <p><a title=\"Billetter og priser på skyss.no\" href=\"https://www.skyss.no/Billettar-og-prisar/billettar-og-prisar2/\" target=\"_blank\" rel=\"noopener\">Se informasjon om priser og betingelser for Skyss-billetter på skyss.no</a></p> <p>For Skyss periodebilletter kjøpt i Vy-appen gjelder Skyss sine billettbestemmelser, mens det er Vys Transportvilkår som gjelder for togreisen.</p> <h4>Bagasjeregler</h4> <ul> <li>I Vys tog gjelder Vys regler for sykkel og dyr på tog.</li> <li>Barnevogn med barn kan du ta med gratis – personalet avgjør hvor mange det er plass til.</li> </ul>" };
            FAQ faq32 = new FAQ() { Id = 32, Spørsmål = "Hvordan er samarbeidet mellom Vy og Brakar?", UnderkategoriId = 3, Rating = 0, Svar = "<p>I Buskerud har Vy samarbeid med Brakar om Ungdomsbilletten. Ungdomsbilletten gjelder for lokal reise i Buskerud fylke på alle Vy lokaltog og korte regiontog for personer under 20 år. Ungdomsbillett finnes på Brakars app og på Brakars reisekort/ungdomskort. Ungdomsbilletten er personlig og utstedes av busselskapene/fylkeskommunen.</p> <p>Vy sin periodebillett kan kombineres med overgangsbillett i én sone i Buskerud. Billettene kan kjøpes i Vy-appen.</p>" };
            FAQ faq33 = new FAQ() { Id = 33, Spørsmål = "Hvordan er samarbeidet mellom Vy og AtB?", UnderkategoriId = 3, Rating = 0, Svar = "<p>I Trøndelag har Vy og AtB et billettsamarbeid på strekningen Hommelvik–Ler. AtB sine reisekort, t:kort, er gyldige på tog på denne strekningen. Vy selger ikke egne periodebilletter innenfor billettsamarbeidsområdet. I Sør-Trøndelag forøvrig er det Vy sine priser som gjelder.</p>" };
            FAQ faq34 = new FAQ() { Id = 34, Spørsmål = "Hvordan er samarbeidet mellom Vy og Vestfold Kollektivtrafikk?", UnderkategoriId = 3, Rating = 0, Svar = "<p>Vy tilbyr en tilslutningsbillett til buss i Vestfold for å bidra til sømløse reiser med tog og buss slik at pendlere til og fra Vestfold kan reise med én billett.</p> <p>Billetten selges kun i kombinasjon med Vys 30-dagers periodebillett i Vy-appen. Tilslutningen heter «Vestfoldkortet», har tilsvarende gyldighet som Vestfoldkortet, selges til rabattert pris og gir adgang til alle busslinjer hos Vestfold Kollektivtrafikk (VKT).</p> <p>Tilslutningsbilletten gjelder også for reisende til/fra Drammen som reiser med VKT til Svelvik/Leirvik. Tilslutningsbilletten kan kombineres med «Buss Grenl/Notodden» (tilslutningsbillett i Telemark) eller «Overgang Ruter sone 1» (tilslutningsbillett i Oslo).&nbsp;</p>" };
            FAQ faq35 = new FAQ() { Id = 35, Spørsmål = "Hvordan er samarbeidet mellom Vy og Telemark fylkeskommune?", UnderkategoriId = 3, Rating = 0, Svar = "<p>Vy tilbyr en tilslutningsbillett til buss i Telemark for å bidra til sømløse reiser for pendlere til og fra Telemark.</p> <p>Billetten selges kun i kombinasjon med Vys 30-dagers periodebillett i Vy-appen. Tilslutningen heter «Buss Grenl/Notodden», og er gyldig på buss i Grenland og Notodden.</p> <p>Tilslutningsbilletten kan kombineres med «Vestfoldkortet» (tilslutningsbillett i Vestfold) eller «Overgang Ruter sone 1» (tilslutningsbillett i Oslo).&nbsp;</p>" };

            FAQ faq36 = new FAQ() { Id = 36, Spørsmål = "Hvordan kan jeg endre eller refundere billetten?", UnderkategoriId = 4, Rating = 0, Svar = "<p>Hvis du har enkeltbillett og vil endre eller refundere billetten din, så kan du gjøre det gratis frem til 24 timer før avgang. Ved mindre enn 24 timer før avgang må du betale et gebyr. Miniprisbilletter kan ikke endres eller refunderes.</p> <h4>Angrefrist ved kjøp av billetter</h4> <p>Ved kjøp av enkeltbillett i på vy.no, i appen eller på billettautomat har du en angrefrist på fire timer fra kjøpstidspunkt, såfremt det er før togets avgangstid. Du må ikke betale refusjonsgebyr. Du går frem som beskrevet under for å få kansellert og refundert billetten.</p> <h4>Slik endrer eller refunderer du billett:</h4> <ul> <li>For billett i app: Refunder billetten og kjøp ny billett til avgangen du skal reise med</li> <li>For billett kjøpt på vy.no: Logg deg inn på Min side for refusjon av billett under «Mine reiser» og kjøp ny billett til avgangen du skal reise med</li> <li>Alternativt kan du ta kontakt med kundeservice</li> </ul> <p>Betalte du for billetten med Vy gavekort, må du kontakte kundeservice for å få refusjon.</p> <p><a title=\"Se refusjonsbetingelsene\" href=\"/vilkar-og-personvern/refusjonsbetingelser-for-togbilletter\" target=\"_top\">Se refusjonsbetingelsene</a></p>" };
            FAQ faq37 = new FAQ() { Id = 37, Spørsmål = "Hvordan endrer jeg navn på billetten?", UnderkategoriId = 4, Rating = 0, Svar = "<p>Når du kjøper billetter så er det navnet til den som bestiller som står på billettene. Det er altså ikke noe i veien for at det står samme navn på flere billetter, så lenge personen som bestilte billettene skal være med på reisen.</p> <p>Hvis ikke personen som står oppført på billettene blir med på reisen, ring eller chat med&nbsp;<a title=\"kundeservice\" href=\"/kundeservice/hjelp-og-kontakt\">kundeservice</a>&nbsp;for å endre navn.</p>" };
            FAQ faq38 = new FAQ() { Id = 38, Spørsmål = "Hvordan endrer jeg setereservasjonen min?", UnderkategoriId = 4, Rating = 0, Svar = "<p>For å endre setevalg på en betalt enkeltbillett må du først avbestille billetten. Du kan da kjøpe ny billett med ønsket setevalg. Du kan også kontakte ombordpersonalet på reisen – dersom de har tilgjengelige seter som passer deg bedre kan du benytte deg av disse ledige setene.</p> <p>For å endre setereservasjonen på en periodebillett på et kort regiontog avbestiller du setet du har reservert, og reserverer et nytt. <a title=\"Les mer om hvordan setereservasjon fungerer\" href=\"/alt-om-reisen/tog/billetter/setereservasjon-pa-korte-regiontog\" target=\"_top\">Les mer om hvordan setereservasjon fungerer</a>.</p> <p>Ville du endre navn på setereservasjonen? <a title=\"Se spørsmål om å endre navn på billetten\" href=\"/kundeservice/sporsmal-og-svar/endring-refusjon-og-kontrollgebyr?item=1850\" target=\"_top\">Se spørsmål om å endre navn på billetten</a>.</p>" };
            FAQ faq39 = new FAQ() { Id = 39, Spørsmål = "Toget mitt var forsinket – hvilken erstatning har jeg krav på og hvordan søker jeg om dette?", UnderkategoriId = 4, Rating = 0, Svar = "<p>Du har rett til å få 50 prosent av billettprisen refundert ved:</p> <ul> <li>Forsinkelse på over én time med Vys tog mellom Oslo og Trondheim, Oslo og Bergen, Oslo og Kristiansand/ Stavanger, samt Trondheim og Bodø.</li> <li>Forsinkelse på over 30 minutter på alle andre tog. Reisende med periodebillett gis refusjon for billettprisen etter samme prinsipp som nevnt ovenfor, men delt på antall dager periodebilletten er gyldig for. Maksimal samlet refusjon for en periodebillett er begrenset oppad til 50 prosent av billettens kjøpspris.</li> </ul> <p>Du kan ikke kreve refusjon av billettprisen dersom du ble varslet om forsinkelser på 30 minutter/én time (se regel over) før billetten ble kjøpt.</p> <p>I situasjoner der du må bruke alternativ transport eller får andre utgifter i forbindelse med forsinkelse eller kansellering av tog, kan du få refundert hele eller deler av utleggene. Dette må gjøres skriftlig med vedlagt dokumentasjon innen tre måneder etter hendelsen.</p> <p><a title=\"Søk om prisavslag eller erstatning\" href=\"/kundeservice/prisavslag-og-erstatning\" target=\"_top\">Søk om prisavslag eller erstatning</a>.</p>" };
            FAQ faq40 = new FAQ() { Id = 40, Spørsmål = "Kan jeg få erstatning for merkostnader ved forsinkelse?", UnderkategoriId = 4, Rating = 0, Svar = "<p>I situasjoner der du må ta i bruk alternativ transport eller får andre utgifter i forbindelse med forsinkelse eller kansellering av tog, kan du få refundert hele eller deler av utleggene. Dette må gjøres skriftlig med vedlagt dokumentasjon innen tre måneder etter hendelsen.</p> <p><a title=\"Søk om prisavslag eller erstatning\" href=\"/kundeservice/prisavslag-og-erstatning\" target=\"_top\">Søk om prisavslag eller erstatning</a></p>" };
            FAQ faq41 = new FAQ() { Id = 41, Spørsmål = "Jeg har fått kontrollgebyr på toget– hvordan klager jeg på denne?", UnderkategoriId = 4, Rating = 0, Svar = "<p><a title=\"Her kan du lese mer om kontrollgebyr og sende en klage\" href=\"/kundeservice/kontrollgebyr\" target=\"_top\">Her kan du lese mer om kontrollgebyr og sende en klage</a>.</p>" };
            FAQ faq42 = new FAQ() { Id = 42, Spørsmål = "Jeg rakk ikke toget. Kan jeg få refundert billetten?", UnderkategoriId = 4, Rating = 0, Svar = "<p>Alle billetter må refunderes før avgang, du vil ikke ha krav på refusjon etter togavgangen dersom du ikke rekker toget ditt.</p>" };
            FAQ faq43 = new FAQ() { Id = 43, Spørsmål = "Hvordan endrer jeg betalingskortet tilknyttet Vy-profilen min?", UnderkategoriId = 4, Rating = 0, Svar = "<p>Du kan slette og legge til betalingskort ved å logge inn på vy.no og gå til «Min profil». I appen går du til «Profil», så velger du «Betaling».</p>" };

            FAQ faq44 = new FAQ() { Id = 44, Spørsmål = "Jeg har glemt noe om bord. Hvordan kontakter jeg dere om hittegods?", UnderkategoriId = 5, Rating = 0, Svar = "<p><a title=\"Her får du vite om hittegods\" href=\"/kundeservice/hittegods\" target=\"_top\">Her får du vite om hittegods</a>.</p>" };
            FAQ faq45 = new FAQ() { Id = 45, Spørsmål = "Hvor mye bagasje kan jeg ha med?", UnderkategoriId = 5, Rating = 0, Svar = "<p>Du kan ta med deg inntil 30 kilo fordelt på maksimum 3 kolli. Har du mer enn dette og skal reise mellom Oslo og Voss/Bergen eller Trondheim, kan du benytte <a title=\"bagasjetransport\" href=\"/kundeservice/sporsmal-og-svar/bagasje-og-spesielle-behov?item=682\" target=\"_top\">bagasjetransport</a>.&nbsp;</p>" };
            FAQ faq46 = new FAQ() { Id = 46, Spørsmål = "Kan jeg ha med kjæledyr?", UnderkategoriId = 5, Rating = 0, Svar = "<p>Dyrene som får bli med på tog er hund, katt, gnagere i bur, burfugl og små skilpadder. Du kan ta med akvariefisk så lenge de er forsvarlig pakket. Andre dyr er ikke ønsket på toget. Eksempler på dyr som ikke er ønsket er insekter, edderkopper, krypdyr/reptiler og amfibier.</p> <p>Vil du ha med kjæledyr på bussreise? <a title=\"Les om kjæledyr på bussen på nettbuss.no\" href=\"https://www.nettbuss.no/dyr-pa-bussen/\" target=\"_blank\" rel=\"noopener\">Les om kjæledyr på nettbuss.no</a>.</p> <h4>Førerhund</h4> <p>Førerhund for blinde og svaksynte som har offentlig godkjenning reiser gratis når den er i tjeneste. Dette forutsetter at den er utstyrt med sele og håndtak for den svaksynte. Førerhund under opplæring (sammen med trener) reiser gratis når den er utstyrt med sele og håndtak. Servicehunden må ha på seg vesten sin slik at konduktøren ser at hunden er i tjeneste.</p> <h4>Billett for kjæledyr</h4> <p>For kjæledyr med skulderhøyde over 40 cm er prisen 50 prosent av en ordinær voksenbillett. Kjæledyr med skulderhøyde under 40 cm reiser gratis sammen med deg.</p> <h4>Plass om bord</h4> <p>Det er et begrenset antall plasser for kjæledyr på hver togreise. Plassene finnes i egne merkede områder og det kan derfor være flere dyr i området samtidig.</p> <p>Dersom du reiser med en liten hund (skulderhøyde under 40 cm), betaler du ikke for hunden. Den må da ligge på gulvet foran føttene dine. Dette gjelder både i tog med og uten setereservasjon.</p> <p>Vi anbefaler at du tar med et sklisikkert teppe som hunden kan ligge og sitte på, da gulvet om bord er glatt.</p> <p>Hunder og andre dyr kan ikke tas med på Komfort, Sove, kaféen, familievogn og i dyrefri sone. Det er unntak for førerhund og servicehund som er tillatt i Komfort, Sove og i familievogn i tilknytning til bestilt rullestolplass.</p> <p>Ved alternativ transport kan du gjerne reise med kjæledyret ditt som normalt. Da mange turbusser har teppegulv hvor dyrehår kan sette seg fast og skape problemer for allergikere senere, kan du imidlertid oppleve at enkelte busser ikke kan ha kjæledyr om bord. I disse tilfellene kan det bli ekstra ventetid mens vi organiserer annen transport.</p> <h4>Hunder fra Norske Redningshunder</h4> <p>Hunder som trenes i Norske Redningshunder og skal på kurs eller er i tjeneste, reiser gratis. Det vil si når den er merket med merketrekant. Gyldig medlemskontingent i Norske Redningshunder må også fremvises.</p> <h4>Reise med dyr til eller fra Sverige</h4> <p>Vi gjør oppmerksom på at dersom du skal reise til eller fra Sverige med dyr, er det viktig at du på forhånd setter deg godt inn i reglene til tollvesenet slik at du unngår at dyret blir satt i karantene.</p> <p>Hunder må ha ID-merke, eget pass og dokumentert behandling mot revens dvergbendelorm. Behandlingen mot bendelorm skal være gitt av veterinær, senest 24 timer før hunden passerer grensen over til Norge. Veterinæren skal dokumentere behandlingen i hundens pass. Kravene gjelder også for deg som bare skal på dags- og helgetur til Sverige. For informasjon om andre dyr, <a title=\"se mattilsynet.no\" href=\"https://www.mattilsynet.no/dyr_og_dyrehold/reise_med_kjaledyr/veileder_slik_reiser_du_med_kjaledyret_ditt/?self=true&amp;young=false&amp;dogs=1&amp;cats=1&amp;polecats=1&amp;countriesSource=9817&amp;countries=9817&amp;showRules=showResult#skjema\" target=\"_blank\" rel=\"noopener\">se mattilsynet.no</a>.</p>" };
            FAQ faq47 = new FAQ() { Id = 47, Spørsmål = "Kan jeg ha med sykkel?", UnderkategoriId = 5, Rating = 0, Svar = "<p><a title=\"Les om sykkel på toget\" href=\"/alt-om-reisen/tog/praktisk-informasjon/sykkel-pa-toget\" target=\"_top\">Les om sykkel på toget</a>.</p>" };
            FAQ faq48 = new FAQ() { Id = 48, Spørsmål = "Kan jeg ha med barnevogn?", UnderkategoriId = 5, Rating = 0, Svar = "<p><a title=\"Se svar under temaet «Reise med barn»\" href=\"/kundeservice/sporsmal-og-svar/bagasje-og-spesielle-behov?item=698\" target=\"_top\">Se svar under temaet «Reise med barn»</a>.</p>" };
            FAQ faq49 = new FAQ() { Id = 49, Spørsmål = "Kan jeg ha med ski eller snøbrett?", UnderkategoriId = 5, Rating = 0, Svar = "<p>Ja. Ski og snøbrett skal plasseres på anviste steder, og du er selv ansvarlig for utstyret under reisen. Ski må være pakket forsvarlig.</p>" };
            FAQ faq50 = new FAQ() { Id = 50, Spørsmål = "Kan jeg ha med pulk, rattkjelke eller sparkstøtting?", UnderkategoriId = 5, Rating = 0, Svar = "<p>Pulk, rattkjelke og sparkstøtting er bagasje som på grunn av volum, form og størrelse vanskelig kan plasseres i bagasjehyller om bord i toget. Derfor må du bestille plass på samme måte som for sykkel. Du må betale sykkelbillett, og du er selv ansvarlig for inn- og utlasting.</p> <p><a title=\"Les om reservering av sykkelplass\" href=\"/alt-om-reisen/tog/praktisk-informasjon/sykkel-pa-toget\" target=\"_top\">Les om reservering av sykkelplass</a>.</p>" };
            FAQ faq51 = new FAQ() { Id = 51, Spørsmål = "Kan jeg ha med små elektriske kjøretøy? (Segway, sparkesykkel, o.l.)", UnderkategoriId = 5, Rating = 0, Svar = "<p>Større tohjulinger som Segway kan i sjeldne tilfeller være mulig å ta med som spesialbagasje dersom det er plass. Ta kontakt med kundeservice god tid før reisen dersom du vil at vi skal sjekke om tohjulingen kan bli med på toget.</p> <p>Du kan ta med mindre enhjulinger og ståbrett, elektrisk skateboard og elektrisk sparkesykkel på toget, men du må plassere disse i området avsatt for bagasje. Hvis det ikke er plass der, kan kjøretøyet tas med dersom det ikke er til hinder for konduktør, øvrige passasjerer eller av/påstigning. Du kan ikke plassere kjøretøyet i midtgangen, da dette er rømningsvei.</p> <p>Konduktøren avgjør om kjøretøyet kan tas med om bord i toget.&nbsp;</p> <p>Litium-ion batterier som benyttes i små elektriske kjøretøy skal ikke lades om bord i toget.</p> <p>Små elektriske kjøretøy skal være tydelig merket med produsentens navn, typebetegnelse og fabrikasjonsnummer. Merkingen skal være plassert og utført slik at det er godt synlig.</p>" };
            FAQ faq52 = new FAQ() { Id = 52, Spørsmål = "Kan jeg ha med kontrabass?", UnderkategoriId = 5, Rating = 0, Svar = "<p>Skal du reise langt med regiontog kan du bestille plass til kontrabassen i godsrom dersom det er ledig. Ta kontakt med kundeservice i god tid før reisen for å bestille plass.</p> <p>På korte regiontog og lokaltog kan du ta med kontrabassen dersom det er plass og den kan tas med på en sikker måte.</p> <p>Det er samme pris for å ta med kontrabass som for <a title=\"sykkel\" href=\"/alt-om-reisen/tog/praktisk-informasjon/sykkel-pa-toget\" target=\"_top\">sykkel</a>.</p>" };
            FAQ faq53 = new FAQ() { Id = 53, Spørsmål = "Hva kan jeg ikke ha med om bord?", UnderkategoriId = 5, Rating = 0, Svar = "<p>Dette er eksempler på bagasje du ikke kan ta med:</p> <ul> <li>Ildsfarlige og eksplosive stoffer og annen bagasje som åpenbart kan innebære en sikkerhetsrisiko, eksempelvis moped/motorsykkel, bensindrevne kjøretøy og leker, eller våpen i hylster på kroppen</li> <li>Gjenstander som er for store og uhåndterlige for bagasjehyller eller hattehyller, eksempelvis surfebrett, store sykkelkofferter, kajakk eller kano</li> <li>Dyrebur som er for store for plassen foran setet</li> <li>Elektriske mopeder, scootere og el-scootere</li> <li>Christianiasykkel</li> </ul>" };
            FAQ faq54 = new FAQ() { Id = 54, Spørsmål = "Finnes det oppbevaringsbokser for bagasje på stasjonen?", UnderkategoriId = 5, Rating = 0, Svar = "<p>For informasjon om fasiliteter på stasjonene, se <a title=\"stasjonssidene på banenor.no\" href=\"https://www.banenor.no/Jernbanen/Stasjonssok/\" target=\"_blank\" rel=\"noopener\">stasjonssidene på banenor.no</a>, da det er Bane NOR som drifter stasjonene.</p>" };
            FAQ faq55 = new FAQ() { Id = 55, Spørsmål = "Tilbyr dere bagasjetransport?", UnderkategoriId = 5, Rating = 0, Svar = "<p>Vy tilbyr ikke bagasjetransport. Skulle du ønske å benytte en slik tjeneste, ta kontakt med vår samarbeidspartner <a title=\"porterservice.no\" href=\"https://www.porterservice.no/\" target=\"_blank\" rel=\"noopener\">porterservice.no</a>. Porterservice tilbyr trygg og sikker transport av bagasje på strekningene Oslo–Bergen og Oslo–Trondheim.</p>" };

            FAQ faq56 = new FAQ() { Id = 56, Spørsmål = "Hvordan bestiller jeg billett hvis jeg har spesielle behov for reisen?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Har du spesielle behov for togreisen, er det viktig at du bestiller billetten gjennom kundeservice eller på en betjent stasjon. Tjenester som rullestolplass og gratis plass for førerhund/servicehund kan i dag ikke bestilles på nettet, men funksjonshemmede får likevel tilbud om de ulike rabattene. For rullestolbrukere som ønsker å reise med Sove, har vi en bredere kupé. Denne kupeen må bestilles ved å kontakte kundeservice.</p>" };
            FAQ faq57 = new FAQ() { Id = 57, Spørsmål = "Koster det noe å ha med førerhund/servicehund, rullator eller rullestol?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Et hjelpemiddel som førerhund/servicehund, rullator eller rullestol kan du ta med uten merkostnad, forutsatt at rullestolen ikke overstiger maksimumsmål og vektgrense spesifisert for togreisen. Du må angi størrelse og vekt på rullestolen ved bestilling.</p>" };
            FAQ faq58 = new FAQ() { Id = 58, Spørsmål = "Er det plass om bord til min motoriserte rullestol?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Plassen for rullestoler tilfredsstiller internasjonale mål og er på 70 x 120 cm, så rullestolen din kan ikke være større enn dette. Rullestolen må ikke veie mer enn 250–350 kilo inklusive bruker. Dersom disse målkravene tilfredsstilles, kan rullestolen sikres under transport, og brukeren får tilgang til handikaptoalett, ramper og heiser der det finnes.</p> <p>Rullestolheisenes begrensning varierer mellom togtypene. Dersom vekten på reisende og rullestol totalt overstiger disse begrensningene, vil ikke heisen løfte. Hjelpemidler som skinner har en tålegrense på 250 kilo og ramper på stasjonen har en grense på 300 kilo. Motoriserte rullestoler må være typegodkjent som rullestoler av Statens refusjonsordning for hjelpemidler.</p> <p>Brukere av rullestoler som ikke tilfredsstiller disse kravene kan ikke forventes plass på Vys tog og blir heller ikke tilbudt spesialtransport av Vy.</p> <p>Togene på Trønderbanen og Nabotåget tar dessverre ikke elektriske rullestoler om bord.</p> <p>Noen tog på Rørosbanen har bare mulighet til å ta imot manuelle rullestoler. Kontakt kundeservice for nærmere informasjon om dette.</p>" };
            FAQ faq59 = new FAQ() { Id = 59, Spørsmål = "Hvordan er tilgjengeligheten på stasjonen og plattformen?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Informasjon om tilgjengelighet på stasjoner og adgang til plattform finner du på <a title=\"banenor.no\" href=\"https://www.banenor.no/Jernbanen/Stasjonssok/\" target=\"_blank\" rel=\"noopener\">banenor.no</a>.</p>" };
            FAQ faq60 = new FAQ() { Id = 60, Spørsmål = "Kan jeg få assistanse på stasjonen?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Har du nedsatt funksjonsevne og trenger assistanse på stasjonen for å komme til eller fra toget, kan du få hjelp på følgende stasjoner:</p> <ul> <li>Oslo S</li> <li>Trondheim</li> <li>Bergen</li> <li>Oslo Lufthavn (Gardermoen)*</li> <li>Lillehammer</li> <li>Drammen</li> <li>Asker</li> <li>Sandvika</li> <li>Ski</li> <li>Lillestrøm</li> </ul> <p>*På Oslo Lufthavn er det Oslo Lufthavn og Aviator PRM som står for assistansen. Ring 648 21 900 for nærmere informasjon eller <a title=\"Se nettsidene til Oslo Lufthavn\" href=\"https://avinor.no/flyplass/oslo/\" target=\"_blank\" rel=\"noopener\">se nettsidene til Oslo Lufthavn</a>.</p> <p>Assistanse bestilles hos Bane NOR. For å være garantert å få tjenesten til riktig tidspunkt må du bestille den minst 24 timer på forhånd. Tjenesten er gratis. Stasjonsassistanse kan bestilles via e-post eller hos Bane NORs kundesenter på 477 70 098. Du kan også få bestilt assistanse samtidig som du bestiller togbillett ved å kontakte vår kundeservice.</p> <p>Bestiller du på e-post må du oppgi:</p> <ul> <li>Navn</li> <li>Telefonnummer</li> <li>Assistansebehov</li> <li>Avreisestasjon og ønsket møtested på stasjonen</li> <li>Ankomststasjon</li> <li>Tidspunkt for avreise og ankomst</li> <li>For rullestol må vekt og størrelse oppgis (maks. 70 x 120 cm) og om rullestolen kan legges sammen.</li> <li>Eventuelt andre opplysninger</li> </ul> <p>Hvis du bare skal ha assistanse til av- og påstigning er det ikke krav om forhåndsbestilling, men vi anbefaler at du kontakter kundeservice slik at togpersonalet kan få beskjed på forhånd.</p> <p><a title=\"Les mer om assistansetjenesten på banenor.no\" href=\"https://www.banenor.no/Kundesenter/Assistansetjeneste-pa-stasjonene/\" target=\"_blank\" rel=\"noopener\">Les mer om assistansetjenesten på banenor.no</a></p>" };
            FAQ faq61 = new FAQ() { Id = 61, Spørsmål = "Hvordan er tilgjengeligheten om bord på toget?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Det finnes heis, handikaptoalett og festeanordninger for stolen. Eldre tog har tilgang på ramper og skinner, slik at det er større behov for assistanse ved av- og påstigning.</p> <p>I de nye og de fleste gamle togene våre finnes det plasser&nbsp;som er primært for reisende med redusert mobilitet – disse er merket med symboler. Dette kan for eksempel være reisende med bevegelsesproblemer, eldre, kortvokste/barn, gravide, voksne med små barn, synshemmede, osv. Dersom man ser at noen har ekstra behov for å sitte, oppfordres du til å gi fra deg setet ditt. Denne oppfordringen gjelder kun i tog uten setereservasjon.</p> <h4>Trondheim S–Værnes</h4> <p>Togene på Trønderbanen er utformet slik at det dessverre ikke er mulig for oss å ta med elektriske rullestoler. Togene på Nordlandsbanen er derimot utstyrt med rullestolplass, heis og handikaptoalett.</p> <p>Ettermiddagstoget med avgang kl. 16:00 til Mo i Rana stopper på Værnes slik at reisende med rullestoler kan komme seg av. Toget stopper normalt bare for påstigning, og dette tilbudet er ikke synlig i appen eller på nettsidene. Tjenesten må bestilles ved å kontakte kundeservice.</p>" };
            FAQ faq62 = new FAQ() { Id = 62, Spørsmål = "Jeg skal reise med Vy Buss. Hvordan er tilgjengeligheten på bussen?", UnderkategoriId = 6, Rating = 0, Svar = "<p><a title=\"Les om tilgjengelighet for funksjonshemmede på nettbuss.no\" href=\"https://www.nettbuss.no/rullestol\" target=\"_blank\" rel=\"noopener\">Les om tilgjengelighet for funksjonshemmede på nettbuss.no</a>.</p>" };
            FAQ faq63 = new FAQ() { Id = 63, Spørsmål = "Det er satt opp buss for tog på min reise. Hvordan er tilgjengeligheten om bord på bussen?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Du kan ikke forvente at rullestolen får plass inni bussen. Rullestolen må legges sammen og plasseres i bagasjerommet hvis du kan gå inn eller løftes inn i bussen. Vi transporterer kun rullestoler som er innenfor målene 70 x 120 cm. Hvis du ikke kan gå ut eller løftes ut av rullestolen forhåndsbestiller vi maxitaxi med rullestolrampe.</p> <p>Husk å bestille billetten din gjennom kundeservice eller betjent stasjon. Slik kan vi enklere ivareta behovene dine dersom det blir alternativ transport.</p>" };
            FAQ faq64 = new FAQ() { Id = 64, Spørsmål = "Kan jeg ta med førerhund eller servicehund?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Førerhund for blinde og svaksynte som har offentlig godkjenning reiser gratis når den er i tjeneste. Dette forutsetter at den er utstyrt med sele og håndtak for den svaksynte. Førerhund under opplæring (sammen med trener) reiser gratis når den er utstyrt med sele og håndtak.</p> <p>Servicehunden må ha på seg vesten sin slik at konduktøren ser at hunden er i tjeneste.</p>" };
            FAQ faq65 = new FAQ() { Id = 65, Spørsmål = "Får jeg rabatt på billetten?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Rullestolbrukere kjører gratis med tog som ikke er plassregulert innenfor grensene til Oslo og Akershus (Ruter). Det vil si at det holder å kjøpe billett for den delen av reisen som er utenfor Oslo og Akershus.</p> <p>Reiser du med ledsager og har ledsagerbevis fra kommunen, får du 50 prosent rabatt av ordinær voksenbillett for både kortholder og ledsager.</p>" };
            FAQ faq66 = new FAQ() { Id = 66, Spørsmål = "Hvilke rabattregler gjelder for ledsager?", UnderkategoriId = 6, Rating = 0, Svar = "<p>Dersom du reiser med ledsager og har et nasjonalt ledsagerbevis for funksjonshemmede utstedt av kommunen, gir vi 50 prosent rabatt på ordinær voksenbillett til deg som kortholder, og 50 prosent på ordinær voksenbillett til din ledsager. Hvem som er ledsager er i denne ordningen valgfritt for eieren av ledsagerbeviset. Tilleggslegitimasjon ut over ledsagerbeviset kreves ikke. Ledsagerbeviset gir ingen selvstendig rabatt til kortholder dersom vedkommende reiser alene uten ledsager.</p> <p>For å få rabatten man har krav på som reisende med nasjonalt ledsagerbevis må du velge Honnør som passasjerkategori når du kjøper billett.</p> <p>Blinde med medlemsbevis fra Norges blindeforbund kan ta med ledsager på samme vilkår som nasjonalt ledsagerbevis.</p> <p>Døvblinde med legitimasjon fra kompetansesenter for døvblinde (grønt kort) får honnørrabatt og fri reise for en ledsager. Ta kontakt med kundeservice, betjent stasjon eller ombordpersonalet for å kjøpe billett.</p>" };
            FAQ faq67 = new FAQ() { Id = 67, Spørsmål = "Hva er rettighetene for reisende med nedsatt funksjonsevne?", UnderkategoriId = 6, Rating = 0, Svar = "<p><a title=\"Les vilkårene «Om retten til transport for personer med nedsatt funksjonsevne»\" href=\"/vilkar-og-personvern/rettigheter-for-reisende-med-nedsatt-funksjonsevne\" target=\"_top\">Les vilkårene «Om retten til transport for personer med nedsatt funksjonsevne»</a>.</p>" };

            FAQ faq68 = new FAQ() { Id = 68, Spørsmål = "Kan jeg ha med barnevogn?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Du kan ta med én barnevogn pr. voksen – helt gratis. Det er barnevognplasser i alle tog. Husk å bestille på forhånd i tog med setereservasjon. Du kan ha med deg en sammenslått barnevogn som bagasje.</p> <p>I Oslo og Akershus avgjør personalet hvor mange barnevogner det er plass til.</p> <p>Skal du reise med Vy Buss? Du kan ta med barnevogn gratis dersom det er plass. Sjåføren avgjør hvor barnevognen skal plasseres under reisen.</p> <h4>Barnevognplass&nbsp;</h4> <ul> <li>I dette området i toget er det tillatt å ha med seg barnevogn.</li> <li>Du er garantert plass til barnevognen, og sitter selv helt i nærheten, forutsatt at du har reservert sete.</li> <li>Dette tilbudet er gratis.</li> <li>Kun én voksen kan sitte ved barnevognen, andre reisende sitter i nærheten.</li> </ul> <p>Det er ikke nødvendig å bestille sitteplass til barnet når barnet skal bruke barnevognen under reisen.</p> <p>Det er ikke mulig å reservere barnevognplasser på strekningen Oslo–Gøteborg.</p>" };
            FAQ faq69 = new FAQ() { Id = 69, Spørsmål = "Hvordan får vi plass i Familie?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Familievogn finnes på disse strekningene</p> <ul> <li>Oslo–Trondheim over Dovre</li> <li>Oslo–Kristiansand–Stavanger</li> <li>Oslo–Bergen</li> <li>Trondheim–Bodø</li> </ul> <p>Oppgi antall voksne og barn når du kjøper billett for disse strekningene – da vil du senere i reiseplanleggeren få spørsmål om du ønsker plass i Familie. Dersom du oppgir at du har med barnevogn, vil du automatisk få tildelt plass i Familie.</p>" };
            FAQ faq70 = new FAQ() { Id = 70, Spørsmål = "Hva koster barnebillett? Er det familierabatt?", UnderkategoriId = 7, Rating = 0, Svar = "<p><a title=\"Les om rabatter\" href=\"/alt-om-reisen/tog/billetter/rabatter\" target=\"_top\">Les om rabatter</a>.</p>" };
            FAQ faq71 = new FAQ() { Id = 71, Spørsmål = "Barnet mitt skal reise alene. Hvordan fungerer det?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Vi tilbyr ikke reiseassistanse til barn som reiser alene. Personalet om bord på togene har dessverre ikke anledning til å ta seg av barn som reiser alene, derfor er det viktig at barna er modne nok til å klare seg alene. Personalet har heller ingen lovpålagt plikt til å ta spesielt vare på ditt barn under reisen.</p> <p>Vi har ikke en nedre aldersgrense for barn som reiser uten følge med voksne. Det er derfor opp til foreldre å avgjøre hvorvidt et barn er skikket eller ikke, tatt i betraktning at uforutsette hendelser kan skje.</p> <h4>Noen råd for reisen:</h4> <ul> <li>forbered barna på hva det vil si å reise alene</li> <li>lag avtaler for hva barna skal gjøre om det oppstår noe på reisen</li> <li>ha fulladet mobiltelefon med riktige mobilnumre</li> <li>vær tilgjengelig for barnet underveis og når toget kommer frem</li> <li>reis gjerne sammen med barnet de første gangene slik at både barn og voksne blir trygge</li> </ul>" };
            FAQ faq72 = new FAQ() { Id = 72, Spørsmål = "Har dere en barnemeny?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Ja. <a title=\"Se menyen\" href=\"/alt-om-reisen/tog/praktisk-informasjon/kafe\" target=\"_top\">Se menyen</a>.</p>" };
            FAQ faq73 = new FAQ() { Id = 73, Spørsmål = "Får barnet mitt barnesete hvis vi må ta buss for tog?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Du er dessverre ikke garantert barnesete når det er satt opp alternativ transport. Alle busser registrert fra 2007 til 2012 skal ha to barneseter om bord. Ofte bestiller vi taxi hvis det ikke er barneseter i bussen, men vi kan ikke garantere taxi.</p>" };
            FAQ faq74 = new FAQ() { Id = 74, Spørsmål = "Er det plass til barnevognen hvis vi må ta buss for tog?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Du er dessverre ikke garantert barnesete når det er satt opp alternativ transport. Alle busser registrert fra 2007 til 2012 skal ha to barneseter om bord. Ofte bestiller vi taxi hvis det ikke er barneseter i bussen, men vi kan ikke garantere taxi.</p>" };
            FAQ faq75 = new FAQ() { Id = 75, Spørsmål = "Kan jeg varme opp babymat og flasker om bord?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Ja, det kan du gjøre i kafeen, dersom det er kafé på din togstrekning. Kaféen finner du på Dovrebanen (Oslo–Trondheim), Sørlandsbanen (Oslo–Stavanger), Bergensbanen (Oslo–Bergen) og Nordlandsbanen (Trondheim–Bodø).</p>" };
            FAQ faq76 = new FAQ() { Id = 76, Spørsmål = "Er det stellebord på toget?", UnderkategoriId = 7, Rating = 0, Svar = "<p>Det er stellebord på lange regiontog, som for eksempel Oslo–Trondheim, Oslo–Stavanger, Oslo–Bergen og Trondheim–Bodø. Noen togavganger på Østfoldbanen (inkludert alle Flirttog) og Rørosbanen har også stellebord. Lokaltog har ikke stellebord, med unntak av Flirttog.</p>" };

            FAQ faq77 = new FAQ() { Id = 77, Spørsmål = "Hvordan finner jeg ut om toget mitt er i rute?", UnderkategoriId = 8, Rating = 0, Svar = "<p><a title=\"Sjekk om toget ditt er i rute\" href=\"https://www.vy.no/trafikk-og-ruter/trafikkinfo\" target=\"_top\">Sjekk om toget ditt er i rute</a> her på vy.no, søk på avgangen i appen eller abonner på pushvarsling om avvik i appen. Ellers skal du også få beskjed på tavler og over høyttaler på stasjonen om eventuelle avvik. Personalet om bord skal også holde deg informert.</p>" };
            FAQ faq78 = new FAQ() { Id = 78, Spørsmål = "Hvordan finner jeg rutetidene?", UnderkategoriId = 8, Rating = 0, Svar = "<p>Du kan raskt og enkelt gjøre et rutesøk i <a title=\"reiseplanleggeren\" href=\"/kjop-billetter/togbilletter\" target=\"_top\">reiseplanleggeren</a> eller i appen. Du kan også finne rutetider i <a title=\"rutetabellene\" href=\"/trafikk-og-ruter/rutetider\" target=\"_top\">rutetabellene</a>.</p>" };
            FAQ faq79 = new FAQ() { Id = 79, Spørsmål = "Reisen min påvirkes av arbeider – hvor finner jeg mer informasjon?", UnderkategoriId = 8, Rating = 0, Svar = "<p><a title=\"Se informasjon om planlagte arbeider\" href=\"https://www.vy.no/trafikk-og-ruter/trafikkinfo\" target=\"_top\">Se informasjon om planlagte arbeider</a>.</p> <p>Informasjon om planlagte arbeider finnes også i <a title=\"reiseplanleggeren\" href=\"/kjop-billetter/togbilletter\" target=\"_top\">reiseplanleggeren</a> og i appen når du søker opp en avgang.</p>" };
            FAQ faq80 = new FAQ() { Id = 80, Spørsmål = "Hvordan kommer jeg meg til flyplassen?", UnderkategoriId = 8, Rating = 0, Svar = "<p>Du kan ta toget hyppig, raskt og rimelig til Oslo Lufthavn (Gardermoen), Trondheim Lufthavn (Værnes) og Torp Lufthavn (Sandefjord).</p> <p><a title=\"Les mer om tog til flyplassen\" href=\"/trafikk-og-ruter/stasjoner-og-linjekart/tog-og-buss-til-flyplassen\" target=\"_top\">Les mer om tog til flyplassen</a>.</p> <p>Du kan også ta bussen til Oslo Lufthavn og Trondheim Lufthavn. <a title=\"Se informasjon om flybusser på nettbuss.no\" href=\"https://www.nettbuss.no/flybuss/\" target=\"_blank\" rel=\"noopener\">Se informasjon om flybusser på nettbuss.no</a>.</p>" };
            FAQ faq81 = new FAQ() { Id = 81, Spørsmål = "Hvor finner jeg informasjon om stasjonene?", UnderkategoriId = 8, Rating = 0, Svar = "<p><a title=\"Se åpningstider og adresser for våre betjente stasjoner\" href=\"/trafikk-og-ruter/stasjoner-og-linjekart/stasjoner\" target=\"_top\">Se åpningstider og adresser for våre betjente stasjoner</a>. For øvrig informasjon om stasjoner, <a title=\"se stasjonsoversikten på banenor.no\" href=\"https://www.banenor.no/Jernbanen/Stasjonssok\" target=\"_blank\" rel=\"noopener\">se stasjonsoversikten på banenor.no</a>.</p> <p>For informasjon om stasjonene til Nettbuss, <a title=\"se rutetabeller på nettbuss.no\" href=\"https://www.nettbuss.no/rutetabeller/\" target=\"_blank\" rel=\"noopener\">se rutetabeller på nettbuss.no</a>.</p>" };
            FAQ faq82 = new FAQ() { Id = 82, Spørsmål = "Hvordan parkerer jeg ved stasjonen?", UnderkategoriId = 8, Rating = 0, Svar = "<p><a title=\"stasjoner på banenor.no\" href=\"https://www.banenor.no/Jernbanen/Stasjonssok\" target=\"_blank\" rel=\"noopener\">Se informasjon om stasjonen på banenor.no</a> – velg fanen «Parkering».</p> <p>Med periodebillett kan du parkere rimeligere på stasjoner med egne parkeringsplasser for pendlere. <a title=\"Les om pendlerparkering på banenor.no\" href=\"https://www.banenor.no/Jernbanen/pendlerparkering/\" target=\"_blank\" rel=\"noopener\">Les om pendlerparkering hos banenor.no</a>.</p>" };
            FAQ faq83 = new FAQ() { Id = 83, Spørsmål = "Hvorfor er det så fullt på toget?", UnderkategoriId = 8, Rating = 0, Svar = "<p>I rushtidene er alt vi har av tilgjengelig materiell i bruk. Vi forsøker å matche etterspørselen med «riktig» kapasitet så langt det lar seg gjøre, likevel er det ikke mulig å tilby sitteplasser til alle. Vi får stadig flere togsett levert fra fabrikk så på sikt øker det muligheten til å kjøre flere avganger med doble togsett.</p> <p>Det kan imidlertid være mange grunner til at et tog er veldig fullt. For eksempel om en tidligere avgang ble forsinket eller innstilt kan det føre til at neste tog vil ha flere passasjerer enn vanlig. Noen ganger kan det&nbsp;også være uforutsette hendelser som gjør at vi ikke kan kjøre med det planlagte antall togsett eller vogner. Da blir det ofte satt opp buss i tillegg til toget.</p>" };

            FAQ faq84 = new FAQ() { Id = 84, Spørsmål = "Hvilken mat kan jeg kjøpe om bord?", UnderkategoriId = 9, Rating = 0, Svar = "<p>Hvis toget ditt har kafé, kan vi tilby smakfulle matopplevelser om bord. Sammen med Jostein Medhus, landslagskokk fra Kulinarisk Akademi, har vi utviklet en meny basert på norske smaker og lokale råvarer i sesong. Vi er stolte av å presentere en rekke produsenter som brenner for god smak og egenart. Resultatet er en variert meny med store, små, varme og kalde norske favoritter. Vi har også en barnemeny. På nattoget tilbyr vi en enklere kveldsmeny. Kafeen er åpen under hele turen.</p> <p><a title=\"Se kafémenyen\" href=\"/alt-om-reisen/tog/praktisk-informasjon/kafe\">Se kafémenyen</a>.</p> <p>I tog uten kafé finnes det ofte automater der du kan kjøpe varme drikker, mineralvann, snacks og sunne småretter.</p>" };
            FAQ faq85 = new FAQ() { Id = 85, Spørsmål = "Hvilke tog tilbyr mat?", UnderkategoriId = 9, Rating = 0, Svar = "<p>Kaféen finner du på Dovrebanen (Oslo–Trondheim), Sørlandsbanen (Oslo–Stavanger), Bergensbanen (Oslo–Bergen) og Nordlandsbanen (Trondheim–Bodø).</p> <p>Automater med varme drikker, mineralvann, snacks og sunne småretter finner du om bord på følgende strekninger:</p> <ul> <li>Drammen–Lillehammer</li> <li>Oslo–Halden–Gøteborg</li> <li>Skien–Eidsvoll</li> <li>Raumabanen, Dombås–Åndalsnes</li> <li>Dombås–Hamar</li> <li>Trondheim–Røros–Hamar</li> <li>Trondheim–Mo i Rana</li> <li>Bodø–Mosjøen</li> <li>Bodø–Rognan</li> </ul> <p>Du kan betale med kort i tillegg til mynt i de fleste automatene om bord. Et kaffekoppsymbol markerer hvilke tog som tilbyr automat når du bestiller billett. Vi gjør oppmerksom på at det i noen tog på Rørosbanen kun finnes varmdrikksautomat om bord. </p>" };
            FAQ faq86 = new FAQ() { Id = 86, Spørsmål = "Kan jeg forhåndsbestille mat?", UnderkategoriId = 9, Rating = 0, Svar = "<p>Enten du reiser alene eller i gruppe kan du forhåndsbestille et smakfullt måltid til turen. Du kan velge mellom norsk tapas og flere andre deilige retter fra vårt koldtkjøkken. Bestilling må gjøres 48 timer før avreise.</p> <p><a title=\"Les mer om tilbudet og bestill mat på togservice.no\" href=\"https://togservice.no/vy\" target=\"_blank\" rel=\"noopener\">Les mer om tilbudet og bestill mat på togservice.no</a>.</p>" };
            FAQ faq87 = new FAQ() { Id = 87, Spørsmål = "Kan jeg ta med egen mat om bord?", UnderkategoriId = 9, Rating = 0, Svar = "<p>Ja, det går fint å spise medbragt mat på toget. Ønsker du å nyte alkohol kan du gjøre det i kaféen, men kun alkohol du har kjøpt der.</p>" };

            FAQ faq88 = new FAQ() { Id = 88, Spørsmål = "Hvordan laster jeg ned appen?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Du kan laste ned appen fra <a title=\"App Store\" href=\"https://apps.apple.com/no/app/vy-nsb/id439655098\" target=\"_blank\" rel=\"noopener\">App Store</a> (iOS) eller <a title=\"Google Play\" href=\"https://play.google.com/store/apps/details?id=com.intele.nsbmob.app\" target=\"_blank\" rel=\"noopener\">Google Play</a> (Android).</p>" };
            FAQ faq89 = new FAQ() { Id = 89, Spørsmål = "Hva kan jeg gjøre i appen?", UnderkategoriId = 10, Rating = 0, Svar = "<li>Kjøpe togbilletter og periodebilletter</li> <li>Sjekke togtider</li> <li>Få varsel om endringer eller avvik som påvirker avgangen din</li> <li>Få oppdatert reiseinformasjon</li> <li>Lagre favorittstrekninger</li> <li>Fornye periodebilletten</li> <li>Kjøpe Ruter tilleggsbillett</li> <li>Bruke mobilen som billettbærer</li> <li>Få varsel før periodebilletten utløper</li> <li>Betale med kort eller Vipps</li> </ul>" };
            FAQ faq90 = new FAQ() { Id = 90, Spørsmål = "Kan jeg kjøpe billett for Ruter, Kolumbus og Skyss?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Du kan kjøpe periodebillett for Ruter, Kolumbus og Skyss og enkeltbillett for Ruter.</p>" };
            FAQ faq91 = new FAQ() { Id = 91, Spørsmål = "Hvordan betaler jeg?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Du kan betale med kredittkort eller Vipps.</p>" };
            FAQ faq92 = new FAQ() { Id = 92, Spørsmål = "Jeg har slettet appen og billetten(e) er borte. Hva gjør jeg?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Da må du kontakte kundeservice eller en betjent stasjon slik at vi kan tilbakestille billetten din. Har du periodebillett kan du overføre denne selv ved å laste ned en erstatningsbillett etter å ha installert appen på nytt. Enkeltbilletter kan du også laste ned på nytt frem til dagen før avreise.</p>" };
            FAQ faq93 = new FAQ() { Id = 93, Spørsmål = "Jeg har byttet telefon. Hvorfor må jeg betale for en erstatningsbillett?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Mistet/tapt periodebillett kan erstattes med ny billett hvis det kan bekreftes at gyldig billett er utstedt. Ved overføring av billett til ny enhet lages en erstatningsbillett.</p> <ul> <li>For enkeltbilletter: Du kan selv overføre din enkeltbillett til ny telefon inntil dagen før avreise.</li> <li>For periodebilletter: Du kan selv overføre periodebilletten gratis til ny enhet to ganger per billett. Fra og med tredje gang må du betale et erstatningsgebyr på 100 kr. Årsbilletter kan overføres inntil seks ganger gratis.</li> </ul>" };
            FAQ faq94 = new FAQ() { Id = 94, Spørsmål = "Kan jeg kjøpe periodebillett på vy.no og laste den ned i appen?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Nei, periodebilletter kjøpt på vy.no må lastes ned på et reisekort via en kortleser eller billettautomat. Dette gjelder også hvis du fornyer periodebilletten via din profil.</p> <p>Det er viktig å merke seg at du ikke kan ha samme periodebillett på flere enheter. Har du kjøpt billetten din til reisekortet, kan den ikke overføres til mobilen, eller omvendt.</p> <p>Ønsker du å ha periodebilletten din i appen, må du kjøpe den i appen. </p>" };
            FAQ faq95 = new FAQ() { Id = 95, Spørsmål = "Hva er fordelen med å ha periodebillett i appen?", UnderkategoriId = 10, Rating = 0, Svar = "<ul> <li>Du kan kjøpe alle periodebilletter i appen. I tillegg er det mulig å få kjøpt rabatterte overgangsbilletter til ulike takstområder som Ruter, Brakar, Østfold og VKT.</li> <li>Du kan sette opp pushvarsling på din strekning.</li> <li>Du får tilgang til Vy Underholdning med lydbøker og aviser.</li> <li>Du har større kontroll ved at du selv bestemmer når periodebilletten skal gjelde fra (for eksempel mandag morgen kl. 07:00).</li> <li>Du er ikke avhengig av å måtte gå innom en kortleser eller billettautomat for å aktivere billetten. Det gjør du selv.</li> </ul>" };
            FAQ faq96 = new FAQ() { Id = 96, Spørsmål = "Hvorfor vises ikke billetten i appen?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Trykk på «Billett» og dra fra toppen for å oppdatere billettvisningen din.</p> <p>Dersom billetten fortsatt ikke dukker opp, kan du ta kontakt med kundeservice.</p> <p>Dersom du finner billetten, men ikke får opp noen QR-kode, kan det skyldes at reisen ikke finner sted i dag. QR-kode på enkeltbilletter vil kun være synlig fra og med reisedato. </p>" };
            FAQ faq97 = new FAQ() { Id = 97, Spørsmål = "Hvordan laster jeg ned billettkjøp fra vy.no i appen?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Har du kjøpt billetter på vy.no uten å være logget inn har du mulighet til å hente ut billetten din i appen ved å bruke hentekoden som ligger i e-postkvitteringen.</p> <p>Har du kjøpt billetten på vy.no og vært innlogget i din profil, kan du etter kjøpet velge å sende billetten til din telefon. Dette fungerer kun dersom du fra før har installert appen og har logget inn med samme bruker i appen. </p>" };
            FAQ faq98 = new FAQ() { Id = 98, Spørsmål = "Hva er pushvarsling og hvordan aktiverer jeg dette?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Pushvarsling er en funksjonalitet i appen som gjør at du automatisk får beskjed dersom det er endringer eller avvik som påvirker avgangen på reisen din.</p> <p>Har du periodebillett aktiverer du pushvarsling under «Favoritter». Har du kjøpt en enkeltbillett aktiveres pushvarsling automatisk for reisen.</p>" };
            FAQ faq99 = new FAQ() { Id = 99, Spørsmål = "Hvorfor får ikke jeg aktivert pushvarsling?", UnderkategoriId = 10, Rating = 0, Svar = "<p>For å kunne aktivere pushvarsling i appen må du ha en gyldig periodebillett i appen.</p> <p>Dersom du har billetten din på et reisekort eller i appene til Ruter/Kolumbus/Skyss eller andre selskaper, vil du per i dag ikke kunne sette opp pushvarsling i Vy-appen. Om du ønsker pushvarsling fra Vy, anbefaler vi deg å kjøpe billett i Vy-appen neste gang. </p>" };
            FAQ faq100 = new FAQ() { Id = 100, Spørsmål = "Hvorfor får jeg ikke mer informasjon når jeg klikker på pushmeldingen?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Pushvarsling er fremdeles under utvikling. Vi jobber med å gi utvidet informasjon når du klikker på meldingen. </p>" };
            FAQ faq101 = new FAQ() { Id = 101, Spørsmål = "Kan jeg få refundert mobilbilletten?", UnderkategoriId = 10, Rating = 0, Svar = "<p>Ja, alle ordinære billetter kan refunderes i appen. Refusjonsbetingelsene for billetten din står på selve billetten. <a title=\"refusjonsbetingelser\" href=\"/vilkar-og-personvern/refusjonsbetingelser-for-togbilletter\" target=\"_top\">Les mer om våre refusjonsbetingelser</a>.</p>" };
            FAQ faq102 = new FAQ() { Id = 102, Spørsmål = "Hvilket ansvar har jeg som kunde?", UnderkategoriId = 10, Rating = 0, Svar = "<ul> <li>Du må ha gyldig billett før du går om bord.</li> <li>Skjermen må være leselig og du må ha nok batteri på telefonen/nettbrettet under hele reisen</li> <li>Dersom du har kjøpt billett til andre med din mobil, er det du som disponerer billettene. Mobilen din må derfor være tilgjengelig for reisefølget ditt ved kontroll.</li> </ul>" };

            FAQ faq103 = new FAQ() { Id = 103, Spørsmål = "Hvordan laster jeg ned Underholdningsappen?", UnderkategoriId = 11, Rating = 0, Svar = "<p>Du kan laste den ned i <a title=\"App Store\" href=\"https://apps.apple.com/no/app/vy-underholdning/id845142076\" target=\"_blank\" rel=\"noopener\">App Store</a> (iOS) eller <a title=\"Google Play\" href=\"https://play.google.com/store/apps/details?id=no.shortcut.nsb_pluss\" target=\"_blank\" rel=\"noopener\">Google Play</a> (Android).</p>" };
            FAQ faq104 = new FAQ() { Id = 104, Spørsmål = "Kan jeg laste ned appen uten å ha en billett?", UnderkategoriId = 11, Rating = 0, Svar = "<p>Ja, alle kan laste ned appen. For å få tilgang til lydbøker og aviser må du ha gyldig billett kjøpt på kundeprofilen din enten via vy.no eller i Vy-appen.</p>" };
            FAQ faq105 = new FAQ() { Id = 105, Spørsmål = "Hvorfor får jeg melding om «ikke gyldig billett» selv om jeg har periodebillett?", UnderkategoriId = 11, Rating = 0, Svar = "<p>Enkelte kunder som har periodebillett på reisekort kan oppleve å få melding i underholdningsappen om «ikke gyldig billett» ved nedlasting av aviser og lydbøker, selv om billetten er aktivert på reisekortet. Vi har dessverre ingen umiddelbar løsning på dette problemet, men har du anledning anbefaler vi deg å kjøpe billettene dine i Vy-appen for å få tilgang til aviser og lydbøker.</p>" };
            FAQ faq106 = new FAQ() { Id = 106, Spørsmål = "Hvordan får jeg flyttet over appen til minnekort på Android?", UnderkategoriId = 11, Rating = 0, Svar = "<p>Åpne innstillinger og bla deg ned til applikasjoner og velg appen Vy Underholdning. For å flytte over appen til minnekortet velger du knappen «flytt til SD-kort». Da vil appen med ditt innhold bli flyttet over til minnekortet. Husk at appen forsvinner hvis du tar ut minnekortet fra mobilen.</p>" };
            FAQ faq107 = new FAQ() { Id = 107, Spørsmål = "Hvilken versjon av Android og iOS krever det at jeg har installert på mobilen?", UnderkategoriId = 11, Rating = 0, Svar = "<p>For Android støttes OS versjon 4.0 og senere, for iOS støttes iOS versjon 7.0 og senere.</p>" };
            FAQ faq108 = new FAQ() { Id = 108, Spørsmål = "Hvor lang tid tar nedlastning av lydbøkene?", UnderkategoriId = 11, Rating = 0, Svar = "<p>Det kan ta inntil fem minutter å laste ned en bok, avhengig av hvor du er og hvilken nettilgang du har. Generelt sett går det raskest når du er på trådløst nett og vi anbefaler at du laster ned boken før du går på toget, for eksempel når du er hjemme eller på jobben. Trådløst nett om bord på tog baserer seg på mobilnettet og kan ikke bli bedre enn nettilgangen i området toget kjører. Dette nettet er heller ikke dimensjonert for nedlasting av større filer. 3G og 4G går også bra om du har god dekning.</p>" };
            FAQ faq109 = new FAQ() { Id = 109, Spørsmål = "Hvor store er lydbokfilene/hvor stor plass tar de på mobilen?", UnderkategoriId = 11, Rating = 0, Svar = "<p>En bok er ganske komprimert og er cirka 100-200 MB. Det vil si at de ikke tar spesielt stor plass på mobilen din. Appen i seg selv tar også liten plass, cirka 20 MB.</p>" };
            FAQ faq110 = new FAQ() { Id = 110, Spørsmål = "Hvordan jobber Vy med å forbedre nettdekning på stasjoner og tog?", UnderkategoriId = 11, Rating = 0, Svar = "<p>Alle våre regiontog har trådløst internett om bord. Etter hvert som Bane NOR sikrer mobil- og bredbånddekning i tunneler og andre utilgjengelige områder rundt om i Norge, blir også kvaliteten på mobilt internett bedre. På deler av noen strekninger vil det likevel fortsatt være ustabil dekning. Dette skyldes at dekningen for mobildata er dårlig eller manglende i området toget kjører. Vi anbefaler deg da å prøve igjen litt senere.</p>" };
            FAQ faq111 = new FAQ() { Id = 111, Spørsmål = "Hva skjer med boken når 100 dager er gått og jeg ikke har hørt ferdig?", UnderkategoriId = 11, Rating = 0, Svar = "<p>Lydboken vil slettes fra appen din etter 100 dager. Dette har å gjøre med avtalen vi har med innholdspartnerne våre. Dersom du ønsker lydboken etter dette må du kjøpe den selv.</p>" };

            FAQ faq112 = new FAQ() { Id = 112, Spørsmål = "Er det internett om bord?", UnderkategoriId = 12, Rating = 0, Svar = "<p>I utgangspunktet skal alle våre regiontog ha gratis internett om bord, samtidig som vi nå også tilbyr dette på de fleste lokaltog. Unntakene er enkelte tog på Ski-lokalen, Bratsbergbanen, Arendalsbanen, Jærbanen, Spikkestad–Asker–Lillestrøm og Stabekk–Moss. På Gjøvikbanen er det kun tilgang til internett på vogner med setereservasjon.</p> <p>For å få tilgang til tjenesten må du koble deg opp på det trådløse nettverket NSB_Interaktiv . Der finner du portalen med informasjon de ulike tjenestene om bord. For å få tilgang til internett må du klikke på «Logg på»-knappen. Du trenger ikke lenger å registrere deg eller skrive inn brukernavn og passord.</p> <p>På deler av noen strekninger vil det være ustabil dekning. Dette skyldes at det trådløse nettet kan ha dårlig dekning/mangle dekning. Ofte er den ustabile dekningen lokal, så vi anbefaler deg da å prøve igjen senere. I tiden framover vil Bane NOR sikre mobil- og bredbånddekning i tunneler rundt om i Norge, og da vil også kvaliteten på internett om bord bli bedre og mer stabil.</p>" };
            FAQ faq113 = new FAQ() { Id = 113, Spørsmål = "Er det strømuttak om bord?", UnderkategoriId = 12, Rating = 0, Svar = "<p>På eldre lokaltog er det ikke strømuttak, mens på nye lokaltog (Flirt) er det strømuttak ved alle seter.</p> <p>På tog på strekningene Lillehammer–Oslo–Drammen, Eidsvoll–Oslo–Skien og Oslo–Halden–Gøteborg er det strømuttak ved alle seter, men eldre tog som brukes som innsatstog i rushtiden har strømuttak kun i Komfortavdelingen.</p> <p>På tog på strekningene Oslo–Stavanger, Oslo–Bergen, Oslo–Trondheim og Trondheim–Bodø er det strømuttak ved alle seter.</p> <p>På tog på Trønderbanen, Meråkerbanen, Rørosbanen og Raumabanen er det strømuttak kun ved et fåtall seter.</p> <p>Sovevogner har strømuttak i alle kupeer.</p> <p>Strømuttakene er 230V.</p>" };

